import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Sidebar from './components/Sidebar';
import PlayerList from './components/PlayerList';
import './App.css';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#90caf9',
    },
    secondary: {
      main: '#f48fb1',
    },
  },
});

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [players, setPlayers] = useState([]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleAddPlayer = (playerName, skillLevel, gender) => {
    const newPlayer = {
      id: Date.now(),
      name: playerName,
      skillLevel: skillLevel,
      gender: gender,
      rank: players.length + 1
    };
    setPlayers([...players, newPlayer]);
  };

  const handleUpdatePlayer = (playerId, updates) => {
    setPlayers(players.map(player => 
      player.id === playerId ? { ...player, ...updates } : player
    ));
  };

  const handleDeletePlayer = (playerId) => {
    setPlayers(players.filter(player => player.id !== playerId));
  };

  const handleReorderPlayers = (result) => {
    if (!result.destination) return;

    const items = Array.from(players);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    // Update ranks after reordering
    const updatedItems = items.map((player, index) => ({
      ...player,
      rank: index + 1
    }));

    setPlayers(updatedItems);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex', minHeight: '100vh' }}>
        <Sidebar 
          isOpen={isSidebarOpen}
          onToggle={toggleSidebar}
          onAddPlayer={handleAddPlayer}
        />
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            p: 3,
            transition: 'margin 0.2s',
            marginLeft: isSidebarOpen ? '240px' : 0
          }}
        >
          {!isSidebarOpen && (
            <IconButton
              color="inherit"
              aria-label="open drawer"
              onClick={toggleSidebar}
              edge="start"
              sx={{ mb: 2 }}
            >
              <MenuIcon />
            </IconButton>
          )}
          <PlayerList
            players={players}
            onUpdatePlayer={handleUpdatePlayer}
            onDeletePlayer={handleDeletePlayer}
          />
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
