import React, { useState, useEffect } from 'react';
import {
  Paper,
  Typography,
  IconButton,
  Box,
  TextField,
  Select,
  MenuItem,
  Alert,
} from '@mui/material';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import DragIndicatorIcon from '@mui/icons-material/DragIndicator';

const PlayerList = ({ players, onUpdatePlayer, onDeletePlayer, onReorderPlayers }) => {
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState('');
  const [editSkillLevel, setEditSkillLevel] = useState('');
  const [error, setError] = useState(null);
  const [enabled, setEnabled] = useState(false);

  useEffect(() => {
    // Enable drag and drop after component mounts
    const timeout = setTimeout(() => {
      setEnabled(true);
    }, 0);
    return () => clearTimeout(timeout);
  }, []);

  const handleEdit = (player) => {
    setEditingId(player.id);
    setEditName(player.name);
    setEditSkillLevel(player.skillLevel);
  };

  const handleSave = (playerId) => {
    if (editName.trim()) {
      onUpdatePlayer(playerId, {
        name: editName.trim(),
        skillLevel: editSkillLevel,
      });
      setEditingId(null);
    }
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  const handleDragEnd = (result) => {
    try {
      if (!result.destination) return;
      onReorderPlayers(result);
    } catch (err) {
      setError('Error reordering players. Please try again.');
      console.error('Drag and drop error:', err);
    }
  };

  const getSkillLevelText = (level) => {
    const levels = {
      1: 'Beginner (1.0-2.0)',
      2: 'Novice (2.5-3.0)',
      3: 'Intermediate (3.5-4.0)',
      4: 'Advanced (4.5-5.0)',
      5: 'Expert (5.5+)',
    };
    return levels[level] || 'Unknown';
  };

  if (!enabled) {
    return (
      <div>
        <Typography variant="h4" sx={{ mb: 3 }}>Players Ranking</Typography>
        {players.map((player) => (
          <Paper
            key={player.id}
            sx={{
              p: 2,
              mb: 2,
              display: 'flex',
              alignItems: 'center',
              backgroundColor: '#2d2d2d',
            }}
          >
            <Typography sx={{ mr: 2, minWidth: '30px' }}>
              #{player.rank}
            </Typography>
            <Typography sx={{ flex: 1 }}>{player.name}</Typography>
            <Typography sx={{ mr: 2, color: 'gray' }}>
              {getSkillLevelText(player.skillLevel)}
            </Typography>
            <Typography sx={{ mr: 2, color: 'gray' }}>
              {player.gender}
            </Typography>
          </Paper>
        ))}
      </div>
    );
  }

  return (
    <div>
      <Typography variant="h4" sx={{ mb: 3 }}>Players Ranking</Typography>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="playerList">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {players.map((player, index) => (
                <Draggable
                  key={player.id}
                  draggableId={String(player.id)}
                  index={index}
                >
                  {(provided) => (
                    <Paper
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      sx={{
                        p: 2,
                        mb: 2,
                        display: 'flex',
                        alignItems: 'center',
                        backgroundColor: '#2d2d2d',
                      }}
                    >
                      <div {...provided.dragHandleProps}>
                        <DragIndicatorIcon sx={{ mr: 2, cursor: 'grab' }} />
                      </div>
                      
                      <Typography sx={{ mr: 2, minWidth: '30px' }}>
                        #{player.rank}
                      </Typography>

                      {editingId === player.id ? (
                        <Box sx={{ flex: 1, display: 'flex', gap: 2, alignItems: 'center' }}>
                          <TextField
                            value={editName}
                            onChange={(e) => setEditName(e.target.value)}
                            size="small"
                            sx={{ flex: 1 }}
                          />
                          <Select
                            value={editSkillLevel}
                            onChange={(e) => setEditSkillLevel(e.target.value)}
                            size="small"
                            sx={{ width: 200 }}
                          >
                            <MenuItem value={1}>Beginner (1.0-2.0)</MenuItem>
                            <MenuItem value={2}>Novice (2.5-3.0)</MenuItem>
                            <MenuItem value={3}>Intermediate (3.5-4.0)</MenuItem>
                            <MenuItem value={4}>Advanced (4.5-5.0)</MenuItem>
                            <MenuItem value={5}>Expert (5.5+)</MenuItem>
                          </Select>
                          <IconButton onClick={() => handleSave(player.id)} color="primary">
                            <SaveIcon />
                          </IconButton>
                          <IconButton onClick={handleCancel} color="secondary">
                            <CancelIcon />
                          </IconButton>
                        </Box>
                      ) : (
                        <>
                          <Typography sx={{ flex: 1 }}>{player.name}</Typography>
                          <Typography sx={{ mr: 2, color: 'gray' }}>
                            {getSkillLevelText(player.skillLevel)}
                          </Typography>
                          <Typography sx={{ mr: 2, color: 'gray' }}>
                            {player.gender}
                          </Typography>
                          <IconButton onClick={() => handleEdit(player)}>
                            <EditIcon />
                          </IconButton>
                          <IconButton onClick={() => onDeletePlayer(player.id)} color="error">
                            <DeleteIcon />
                          </IconButton>
                        </>
                      )}
                    </Paper>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default PlayerList;
