import React, { useState } from 'react';
import {
  Paper,
  Typography,
  IconButton,
  Box,
  TextField,
  Select,
  MenuItem,
} from '@mui/material';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import DragIndicatorIcon from '@mui/icons-material/DragIndicator';

const PlayerCard = React.memo(({ player, provided, isDragging, onEdit, onDelete, isEditing, editProps }) => (
  <Paper
    ref={provided.innerRef}
    {...provided.draggableProps}
    sx={{
      p: 2,
      mb: 2,
      display: 'flex',
      alignItems: 'center',
      backgroundColor: '#2d2d2d',
      opacity: isDragging ? 0.5 : 1,
    }}
  >
    <div {...provided.dragHandleProps}>
      <DragIndicatorIcon sx={{ mr: 2, cursor: 'grab' }} />
    </div>
    
    <Typography sx={{ mr: 2, minWidth: '30px' }}>
      #{player.rank}
    </Typography>

    {isEditing ? (
      <Box sx={{ flex: 1, display: 'flex', gap: 2, alignItems: 'center' }}>
        <TextField
          value={editProps.editName}
          onChange={(e) => editProps.setEditName(e.target.value)}
          size="small"
          sx={{ flex: 1 }}
        />
        <Select
          value={editProps.editSkillLevel}
          onChange={(e) => editProps.setEditSkillLevel(e.target.value)}
          size="small"
          sx={{ width: 200 }}
        >
          <MenuItem value={1}>Beginner (1.0-2.0)</MenuItem>
          <MenuItem value={2}>Novice (2.5-3.0)</MenuItem>
          <MenuItem value={3}>Intermediate (3.5-4.0)</MenuItem>
          <MenuItem value={4}>Advanced (4.5-5.0)</MenuItem>
          <MenuItem value={5}>Expert (5.5+)</MenuItem>
        </Select>
        <IconButton onClick={() => editProps.handleSave(player.id)} color="primary">
          <SaveIcon />
        </IconButton>
        <IconButton onClick={editProps.handleCancel} color="secondary">
          <CancelIcon />
        </IconButton>
      </Box>
    ) : (
      <>
        <Typography sx={{ flex: 1 }}>{player.name}</Typography>
        <Typography sx={{ mr: 2, color: 'gray' }}>
          {editProps.getSkillLevelText(player.skillLevel)}
        </Typography>
        <Typography sx={{ mr: 2, color: 'gray' }}>
          {player.gender}
        </Typography>
        <IconButton onClick={() => onEdit(player)}>
          <EditIcon />
        </IconButton>
        <IconButton onClick={() => onDelete(player.id)} color="error">
          <DeleteIcon />
        </IconButton>
      </>
    )}
  </Paper>
));

const DroppableContent = React.memo(({ players, editingId, onEdit, onDelete, editProps }) => (
  <Droppable droppableId="droppable-list">
    {(provided) => (
      <div
        {...provided.droppableProps}
        ref={provided.innerRef}
      >
        {players.map((player, index) => (
          <Draggable
            key={player.id}
            draggableId={String(player.id)}
            index={index}
          >
            {(provided, snapshot) => (
              <PlayerCard
                player={player}
                provided={provided}
                isDragging={snapshot.isDragging}
                onEdit={onEdit}
                onDelete={onDelete}
                isEditing={editingId === player.id}
                editProps={editProps}
              />
            )}
          </Draggable>
        ))}
        {provided.placeholder}
      </div>
    )}
  </Droppable>
));

const PlayerList = ({ players, onUpdatePlayer, onDeletePlayer, onReorderPlayers }) => {
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState('');
  const [editSkillLevel, setEditSkillLevel] = useState('');

  const handleEdit = (player) => {
    setEditingId(player.id);
    setEditName(player.name);
    setEditSkillLevel(player.skillLevel);
  };

  const handleSave = (playerId) => {
    if (editName.trim()) {
      onUpdatePlayer(playerId, {
        name: editName.trim(),
        skillLevel: editSkillLevel,
      });
      setEditingId(null);
    }
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  const getSkillLevelText = (level) => {
    const levels = {
      1: 'Beginner (1.0-2.0)',
      2: 'Novice (2.5-3.0)',
      3: 'Intermediate (3.5-4.0)',
      4: 'Advanced (4.5-5.0)',
      5: 'Expert (5.5+)',
    };
    return levels[level] || 'Unknown';
  };

  const editProps = {
    editName,
    setEditName,
    editSkillLevel,
    setEditSkillLevel,
    handleSave,
    handleCancel,
    getSkillLevelText,
  };

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 3 }}>Players Ranking</Typography>
      <DragDropContext onDragEnd={onReorderPlayers}>
        <DroppableContent
          players={players}
          editingId={editingId}
          onEdit={handleEdit}
          onDelete={onDeletePlayer}
          editProps={editProps}
        />
      </DragDropContext>
    </Box>
  );
};

export default PlayerList;
