import React, { useState } from 'react';
import {
  Drawer,
  IconButton,
  Typography,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';

const Sidebar = ({ isOpen, onToggle, onAddPlayer }) => {
  const [playerName, setPlayerName] = useState('');
  const [skillLevel, setSkillLevel] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (playerName.trim() && skillLevel) {
      onAddPlayer(playerName.trim(), skillLevel);
      setPlayerName('');
      setSkillLevel('');
    }
  };

  return (
    <Drawer
      variant="persistent"
      anchor="left"
      open={isOpen}
      sx={{
        width: 240,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: 240,
          boxSizing: 'border-box',
          backgroundColor: '#1e1e1e',
          color: 'white',
        },
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', p: 2, justifyContent: 'space-between' }}>
        <Typography variant="h6">Player Management</Typography>
        <IconButton onClick={onToggle} sx={{ color: 'white' }}>
          {isOpen ? <ChevronLeftIcon /> : <ChevronRightIcon />}
        </IconButton>
      </Box>

      <Box component="form" onSubmit={handleSubmit} sx={{ p: 2 }}>
        <Typography variant="subtitle1" sx={{ mb: 2 }}>Add New Player</Typography>
        
        <TextField
          fullWidth
          label="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          margin="normal"
          variant="outlined"
          sx={{ mb: 2 }}
        />

        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Skill Level</InputLabel>
          <Select
            value={skillLevel}
            label="Skill Level"
            onChange={(e) => setSkillLevel(e.target.value)}
          >
            <MenuItem value={1}>Beginner (1.0-2.0)</MenuItem>
            <MenuItem value={2}>Novice (2.5-3.0)</MenuItem>
            <MenuItem value={3}>Intermediate (3.5-4.0)</MenuItem>
            <MenuItem value={4}>Advanced (4.5-5.0)</MenuItem>
            <MenuItem value={5}>Expert (5.5+)</MenuItem>
          </Select>
        </FormControl>

        <Button
          type="submit"
          variant="contained"
          fullWidth
          disabled={!playerName.trim() || !skillLevel}
        >
          Add Player
        </Button>
      </Box>
    </Drawer>
  );
};

export default Sidebar;
