import React, { useState } from 'react';
import {
  Typography,
  Box,
  Paper,
  IconButton,
  TextField,
  Select,
  MenuItem,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';

const PlayerList = ({ players, teams, onUpdatePlayer, onDeletePlayer, onMovePlayer, onAssignPlayer }) => {
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState('');
  const [editSkillLevel, setEditSkillLevel] = useState('');
  const [editingTeam, setEditingTeam] = useState(null);

  const handleEdit = (player) => {
    setEditingId(player.id);
    setEditName(player.name);
    setEditSkillLevel(player.skillLevel);
  };

  const handleSave = (playerId) => {
    if (editName.trim()) {
      onUpdatePlayer(playerId, {
        name: editName.trim(),
        skillLevel: editSkillLevel,
      });
      setEditingId(null);
    }
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  const getSkillLevelText = (level) => {
    const levels = {
      1: 'Beginner (1.0-2.0)',
      2: 'Novice (2.5-3.0)',
      3: 'Intermediate (3.5-4.0)',
      4: 'Advanced (4.5-5.0)',
      5: 'Expert (5.5+)',
    };
    return levels[level] || 'Unknown';
  };

  const getTeamName = (teamId) => {
    if (!teamId) return '';
    const team = teams.find(t => t.id === teamId);
    return team ? team.name : '';
  };

  const handleTeamClick = (playerId) => {
    setEditingTeam(playerId);
  };

  const handleTeamChange = (playerId, teamId) => {
    onAssignPlayer(playerId, teamId);
    setEditingTeam(null);
  };

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>Players Ranking</Typography>
      <div className="player-list">
        {players.map((player, index) => (
          <Paper
            key={player.id}
            sx={{
              p: 1,
              mb: 1,
              display: 'flex',
              alignItems: 'center',
              backgroundColor: '#2d2d2d',
              '& .action-buttons': {
                opacity: 0,
                transition: 'opacity 0.2s'
              },
              '&:hover .action-buttons': {
                opacity: 1
              },
              '& .MuiIconButton-root': {
                padding: '4px',
              },
              '& .MuiTypography-root': {
                fontSize: '0.9rem',
              }
            }}
          >
            <Box 
              className="action-buttons"
              sx={{ 
                display: 'flex', 
                flexDirection: 'column',
                mr: 0.5,
                '& .MuiIconButton-root': {
                  marginBottom: '2px',
                }
              }}
            >
              <IconButton 
                size="small" 
                onClick={() => onMovePlayer(index, 'up')}
                disabled={index === 0}
              >
                <ArrowUpwardIcon fontSize="small" />
              </IconButton>
              <IconButton 
                size="small" 
                onClick={() => onMovePlayer(index, 'down')}
                disabled={index === players.length - 1}
              >
                <ArrowDownwardIcon fontSize="small" />
              </IconButton>
            </Box>

            <Typography sx={{ mr: 1, minWidth: '25px' }}>
              #{index + 1}
            </Typography>

            {editingId === player.id ? (
              <Box sx={{ flex: 1, display: 'flex', gap: 1, alignItems: 'center' }}>
                <TextField
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                  size="small"
                  sx={{ 
                    flex: 1,
                    '& .MuiInputBase-root': {
                      height: '32px',
                    }
                  }}
                />
                <Select
                  value={editSkillLevel}
                  onChange={(e) => setEditSkillLevel(e.target.value)}
                  size="small"
                  sx={{ 
                    width: 180,
                    height: '32px'
                  }}
                >
                  <MenuItem value={1}>Beginner (1.0-2.0)</MenuItem>
                  <MenuItem value={2}>Novice (2.5-3.0)</MenuItem>
                  <MenuItem value={3}>Intermediate (3.5-4.0)</MenuItem>
                  <MenuItem value={4}>Advanced (4.5-5.0)</MenuItem>
                  <MenuItem value={5}>Expert (5.5+)</MenuItem>
                </Select>
                <IconButton onClick={() => handleSave(player.id)} color="primary" size="small">
                  <SaveIcon fontSize="small" />
                </IconButton>
                <IconButton onClick={handleCancel} color="secondary" size="small">
                  <CancelIcon fontSize="small" />
                </IconButton>
              </Box>
            ) : (
              <>
                <Box sx={{ flex: 1, display: 'flex', alignItems: 'center' }}>
                  <Typography>{player.name}</Typography>
                  {editingTeam === player.id ? (
                    <Select
                      value={player.teamId || ''}
                      onChange={(e) => handleTeamChange(player.id, e.target.value)}
                      size="small"
                      sx={{ 
                        ml: 1,
                        width: 120,
                        height: '24px',
                        '& .MuiSelect-select': {
                          py: 0
                        }
                      }}
                      onBlur={() => setEditingTeam(null)}
                      autoFocus
                    >
                      <MenuItem value="">No Team</MenuItem>
                      {teams.map(team => (
                        <MenuItem key={team.id} value={team.id}>
                          {team.name}
                        </MenuItem>
                      ))}
                    </Select>
                  ) : player.teamId && (
                    <Typography 
                      onClick={() => handleTeamClick(player.id)}
                      sx={{ 
                        ml: 1,
                        color: 'primary.main',
                        fontSize: '0.8rem',
                        backgroundColor: 'rgba(144, 202, 249, 0.1)',
                        px: 1,
                        py: 0.25,
                        borderRadius: 1,
                        cursor: 'pointer',
                        '&:hover': {
                          backgroundColor: 'rgba(144, 202, 249, 0.2)',
                        }
                      }}
                    >
                      {getTeamName(player.teamId)}
                    </Typography>
                  )}
                </Box>
                <Typography sx={{ mr: 1, color: 'gray' }}>
                  {getSkillLevelText(player.skillLevel)}
                </Typography>
                <Typography sx={{ mr: 1, color: 'gray' }}>
                  {player.gender}
                </Typography>
                <Box className="action-buttons" sx={{ display: 'flex' }}>
                  <IconButton onClick={() => handleEdit(player)} size="small">
                    <EditIcon fontSize="small" />
                  </IconButton>
                  <IconButton onClick={() => onDeletePlayer(player.id)} color="error" size="small">
                    <DeleteIcon fontSize="small" />
                  </IconButton>
                </Box>
              </>
            )}
          </Paper>
        ))}
      </div>
    </Box>
  );
};

export default PlayerList;
