import React, { useState } from 'react';
import {
  Typography,
  Box,
  Paper,
  IconButton,
  TextField,
  Select,
  MenuItem,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';

const PlayerList = ({ players, teams, onUpdatePlayer, onDeletePlayer, onMovePlayer, onAssignPlayer }) => {
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState('');
  const [editSkillLevel, setEditSkillLevel] = useState('');
  const [editingTeam, setEditingTeam] = useState(null);

  const handleEdit = (player) => {
    setEditingId(player.id);
    setEditName(player.name);
    setEditSkillLevel(player.skillLevel);
  };

  const handleSave = (playerId) => {
    if (editName.trim()) {
      onUpdatePlayer(playerId, {
        name: editName.trim(),
        skillLevel: editSkillLevel,
      });
      setEditingId(null);
    }
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  const getSkillLevelText = (level) => {
    const levels = {
      1: 'Beginner (1.0-2.0)',
      2: 'Novice (2.5-3.0)',
      3: 'Intermediate (3.5-4.0)',
      4: 'Advanced (4.5-5.0)',
      5: 'Expert (5.5+)',
    };
    return levels[level] || 'Unknown';
  };

  const getTeamName = (teamId) => {
    if (!teamId) return '';
    const team = teams.find(t => t.id === teamId);
    return team ? team.name : '';
  };

  const handleTeamClick = (playerId) => {
    setEditingTeam(playerId);
  };

  const handleTeamChange = (playerId, teamId) => {
    onAssignPlayer(playerId, teamId);
    setEditingTeam(null);
  };

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>Players Ranking</Typography>
      <div className="player-list">
        {/* Header row */}
        <Box 
          sx={{ 
            display: 'flex',
            px: 1,
            mb: 1,
            color: 'gray',
            '& > *': { fontSize: '0.8rem' }
          }}
        >
          <Box sx={{ width: 60 }}></Box> {/* Space for move buttons */}
          <Box sx={{ width: 30 }}>#</Box>
          <Box sx={{ width: 140 }}>Name</Box>
          <Box sx={{ width: 140 }}>Team</Box>
          <Box sx={{ width: 150 }}>Skill Level</Box>
          <Box sx={{ width: 80 }}>Gender</Box>
          <Box sx={{ width: 80 }}></Box> {/* Space for action buttons */}
        </Box>

        <Box sx={{ 
          position: 'relative',
          mt: 1,
          height: players.length * 41,
          mb: 2
        }}>
          {players.map((player, index) => (
            <Paper
              key={player.id}
              sx={{
                py: 0.5,
                px: 1,
                display: 'flex',
                alignItems: 'center',
                backgroundColor: '#2d2d2d',
                transform: `translateY(${index * 41}px)`, // 41px is the height of each item (32px) + margin (9px)
                transition: 'transform 0.2s ease-in-out',
                top: 0,
                left: 0,
                right: 0,
                height: '32px',
                zIndex: players.length - index, // Higher items appear on top
                '& .action-buttons': {
                  opacity: 0,
                  transition: 'opacity 0.2s'
                },
                '&:hover .action-buttons': {
                  opacity: 1
                },
                '& .MuiIconButton-root': {
                  padding: '4px',
                },
                '& .MuiTypography-root': {
                  fontSize: '0.9rem',
                }
              }}
            >
              <Box sx={{ width: 60, display: 'flex', justifyContent: 'center' }}>
                <Box 
                  className="action-buttons"
                  sx={{ 
                    display: 'flex', 
                    flexDirection: 'column',
                    '& .MuiIconButton-root': {
                      marginBottom: '2px',
                    }
                  }}
                >
                  <IconButton 
                    size="small" 
                    onClick={() => onMovePlayer(index, 'up')}
                    disabled={index === 0}
                  >
                    <ArrowUpwardIcon fontSize="small" />
                  </IconButton>
                  <IconButton 
                    size="small" 
                    onClick={() => onMovePlayer(index, 'down')}
                    disabled={index === players.length - 1}
                  >
                    <ArrowDownwardIcon fontSize="small" />
                  </IconButton>
                </Box>
              </Box>

              <Typography sx={{ width: 30 }}>
                #{index + 1}
              </Typography>

              {editingId === player.id ? (
                <Box sx={{ flex: 1, display: 'flex', gap: 1, alignItems: 'center' }}>
                  <TextField
                    value={editName}
                    onChange={(e) => setEditName(e.target.value)}
                    size="small"
                    sx={{ 
                      width: 140,
                      '& .MuiInputBase-root': {
                        height: '32px',
                      }
                    }}
                  />
                  <Select
                    value={editSkillLevel}
                    onChange={(e) => setEditSkillLevel(e.target.value)}
                    size="small"
                    sx={{ 
                      width: 150,
                      height: '32px'
                    }}
                  >
                    <MenuItem value={1}>Beginner (1.0-2.0)</MenuItem>
                    <MenuItem value={2}>Novice (2.5-3.0)</MenuItem>
                    <MenuItem value={3}>Intermediate (3.5-4.0)</MenuItem>
                    <MenuItem value={4}>Advanced (4.5-5.0)</MenuItem>
                    <MenuItem value={5}>Expert (5.5+)</MenuItem>
                  </Select>
                  <Box sx={{ width: 80, display: 'flex', gap: 0.5 }}>
                    <IconButton onClick={() => handleSave(player.id)} color="primary" size="small">
                      <SaveIcon fontSize="small" />
                    </IconButton>
                    <IconButton onClick={handleCancel} color="secondary" size="small">
                      <CancelIcon fontSize="small" />
                    </IconButton>
                  </Box>
                </Box>
              ) : (
                <>
                  <Typography sx={{ width: 140, overflow: 'hidden', textOverflow: 'ellipsis' }}>
                    {player.name}
                  </Typography>

                  <Box sx={{ width: 140 }}>
                    {editingTeam === player.id ? (
                      <Select
                        value={player.teamId || ''}
                        onChange={(e) => handleTeamChange(player.id, e.target.value)}
                        size="small"
                        sx={{ 
                          width: 140,
                          height: '24px',
                          '& .MuiSelect-select': {
                            py: 0
                          }
                        }}
                        onBlur={() => setEditingTeam(null)}
                        autoFocus
                      >
                        <MenuItem value="">No Team</MenuItem>
                        {teams.map(team => (
                          <MenuItem key={team.id} value={team.id}>
                            {team.name}
                          </MenuItem>
                        ))}
                      </Select>
                    ) : player.teamId ? (
                      <Typography 
                        onClick={() => handleTeamClick(player.id)}
                        sx={{ 
                          color: 'primary.main',
                          fontSize: '0.8rem',
                          backgroundColor: 'rgba(144, 202, 249, 0.1)',
                          px: 1,
                          py: 0.25,
                          borderRadius: 1,
                          cursor: 'pointer',
                          display: 'inline-block',
                          '&:hover': {
                            backgroundColor: 'rgba(144, 202, 249, 0.2)',
                          }
                        }}
                      >
                        {getTeamName(player.teamId)}
                      </Typography>
                    ) : (
                      <Typography 
                        onClick={() => handleTeamClick(player.id)}
                        sx={{ 
                          color: 'gray',
                          fontSize: '0.8rem',
                          cursor: 'pointer',
                          '&:hover': {
                            color: 'primary.main',
                          }
                        }}
                      >
                        Add to team
                      </Typography>
                    )}
                  </Box>

                  <Typography sx={{ width: 150, color: 'gray' }}>
                    {getSkillLevelText(player.skillLevel)}
                  </Typography>

                  <Typography sx={{ width: 80, color: 'gray' }}>
                    {player.gender}
                  </Typography>

                  <Box className="action-buttons" sx={{ width: 80, display: 'flex', justifyContent: 'flex-end' }}>
                    <IconButton onClick={() => handleEdit(player)} size="small">
                      <EditIcon fontSize="small" />
                    </IconButton>
                    <IconButton onClick={() => onDeletePlayer(player.id)} color="error" size="small">
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  </Box>
                </>
              )}
            </Paper>
          ))}
        </Box>
      </div>
    </Box>
  );
};

export default PlayerList;
